import flask 
import cv2
import base64
app = flask.Flask(__name__)
app.config["DEBUG"] = True
import io
import numpy as np
from PIL import Image
import json
from flask import Flask, request

@app.route('/', methods=['GET','POST'])
def home():
    image2 = request.json['img']
    #image_1=''
    base64_decoded = base64.b64decode(image2)
    dataBytesIO = io.BytesIO(base64_decoded)
    imagedec = Image.open(dataBytesIO)
    image = np.array(imagedec)
    #gray=cv2.imread(image,0)
    ##C:\Users\muham\Desktop\Finalize\Cropperjs
    gray=cv2.cvtColor(image,cv2.COLOR_BGR2GRAY)
    th, threshed = cv2.threshold(gray, 100, 255,cv2.THRESH_BINARY_INV|cv2.THRESH_OTSU)

    ## findcontours
    cnts = cv2.findContours(threshed, cv2.RETR_LIST, cv2.CHAIN_APPROX_SIMPLE)[-2]
    

    ## filter by area
    xcnts = []
    for cnt in cnts:
        if cv2.contourArea(cnt):
            xcnts.append(cnt)

    x=("Bristles: {}".format(len(xcnts)))
    return (x)

app.run()